import React from 'react';
import axios from 'axios';

export default class AddUserList extends React.Component {
  state = {
    name: '',
    email:'',
    phone:'',
    website:''
  }

  handleChange = event => {
    this.setState({ name: event.target.value });
    this.setState({ email: event.target.value });
    this.setState({ phone: event.target.value });
    this.setState({ website: event.target.value });
  }

  handleSubmit = event => {
    event.preventDefault();

    const user = {
      name: this.state.name,
      email:this.state.email,
      phone:this.state.phone,
      website:this.state.website,
    };

    axios.post(`https://jsonplaceholder.typicode.com/users`, { user })
      .then(res => {
        console.log(res);
        console.log(res.data);

      })
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div className="Cadusuarios">
                    <h2 id="titulo">Inclus√£o de novo usuario</h2>
                    <div>Nome: </div>
                    <input type="text" name="name" onChange={this.handleChange}/>
                    <div>Email: </div>
                    <input type="email" name="email" onChange={this.handleChange}/>
                    <div>Telefone: </div>
                    <input type="phone" name="phone" onChange={this.handleChange}/>
                    <div>Site: </div>
                    <input type="site" name="website" onChange={this.handleChange}/>
            </div>
          <button type="submit">Add</button>
        </form>
      </div>
    )
  }
}
