import React from "react";
export default class Classe extends React.Component{
    constructor(props){
        super(props)
        this.modelo = 'HRV'
        this.state={
            ligado:false,
            velocidade:0,
        }
    }
    //USANDO STATE DIRETO
    ligadoDesligado = ()=>{
        //this.setState({ligado:true})
        this.setState({ligado:!this.state.ligado})
    }

    //USANDO STATE COM UMA FUNÇÃO, MAIS INDICADO
    OnnOff =()=>{
        this.setState((state)=>(
            {ligado:!state.ligado}
        ))
    }
    acelerar = () =>{
        this.setState((state,props)=>(
            {velocidade:state.velocidade + props.fator}
        ))
    }

    render(){
        return(
            <div>
                <h1>Meu Carro</h1>
                <p>Modelo: {this.modelo}</p>
                <p>Estado: {this.state.ligado?"ligado":"desligado"}</p>
                <p>Velocidade: {this.state.velocidade}</p>
                <button onClick={this.ligadoDesligado}>{this.state.ligado?"Desligar":"Ligar"}</button>
                <button onClick={this.OnnOff}>{this.state.ligado?"Desligar":"Ligar"}OnnOff</button>
                <button onClick={this.acelerar}>Acelerar</button>
            </div>
        )
    }
}
